let timer;
import {Debounce} from './interface'
export function debounce(sample: Debounce) {
  return function () {
    let context = this,
      args = arguments;
    if (timer) clearTimeout(timer);
    if (sample.immediate) {
      let callNow = !timer;
      timer = setTimeout(() => {
        timer = null;
      }, sample.wait);
      if (callNow) sample.func.apply(context, args);
    } else {
      timer = setTimeout(() => {
        sample.func.apply(context, args);
      }, sample.wait)
    }
  }
}
