import {typeOf} from './typeOf'
/**
 * 
 * @param value 要格式化的值
 * @param type 格式化输出类型  comma 每三位逗号分隔； capital 大写汉字
 * @returns 
 */
export function formatNumber(value:any, type?:string):string{
  let t = type || 'comma'
  if(typeOf(value,Number)) {
    if(t === 'comma') {
      return toThousands(value)
    }
    return ''
  } else throw 'Must be of numeric type'
}

function toThousands(num) {
  return num.toString().replace(/(\d)(?=(?:\d{3})+$)/g, '$1,')
}